# PancakeSwap Router address (already in checksum format)
PANCAKE_ROUTER_ADDRESS = Web3.to_checksum_address("0x10ED43C718714eb63d5aA57B78B54704E256024E")

# Token addresses (converted to checksum format)
USDT_ADDRESS = Web3.to_checksum_address("0x55d398326f99059fF775485246999027B3197955")
TOKEN_ADDRESS = Web3.to_checksum_address("0xbf12f69c24e46e5fde275aa6a10c63603f7d0bf8")

# Wallet details
PRIVATE_KEY = "your_private_key"
WALLET_ADDRESS = Web3.to_checksum_address("your_wallet_address")

# Instantiate contract
router = web3.eth.contract(address=PANCAKE_ROUTER_ADDRESS, abi=PANCAKE_ROUTER_ABI)

def swap_tokens():
    try:
        # Define swap parameters
        amount_in_wei = Web3.to_wei(0.0001, 'ether')  # Amount of USDT to swap
        slippage_tolerance = 0.01  # 1% slippage
        deadline = int(time.time()) + 60  # Transaction deadline (60 seconds)

        # Get amounts out (min amount considering slippage)
        amounts_out = router.functions.getAmountsOut(
            amount_in_wei, [USDT_ADDRESS, TOKEN_ADDRESS]
        ).call()
        amount_out_min = int(amounts_out[1] * (1 - slippage_tolerance))

        # Build transaction
        txn = router.functions.swapExactTokensForTokens(
            amount_in_wei, 
            amount_out_min,
            [USDT_ADDRESS, TOKEN_ADDRESS],
            WALLET_ADDRESS,
            deadline
        ).buildTransaction({
            'from': WALLET_ADDRESS,
            'gas': 200000,
            'gasPrice': Web3.to_wei(5, 'gwei'),  # Adjust gas price as needed
            'nonce': web3.eth.getTransactionCount(WALLET_ADDRESS),
        })

        # Sign and send the transaction
        signed_txn = web3.eth.account.sign_transaction(txn, private_key=PRIVATE_KEY)
        txn_hash = web3.eth.sendRawTransaction(signed_txn.rawTransaction)

        print(f"Transaction sent! Txn hash: {web3.toHex(txn_hash)}")
    except Exception as e:
        print(f"Error executing swap: {e}")

# Schedule the swap every 15 minutes
schedule.every(1).minutes.do(swap_tokens)

print("Swapping bot started...")
while True:
    schedule.run_pending()
    time.sleep(1)
